SELECT B.cscoCd, B.cscoNm, A.eqpmNm, A.eqpmIp, A.rqrNm, A.instPos, A.eqpmCd ,(SELECT (SELECT eqpmGrpNm FROM EqpmGrpTbl WHERE N.grpPath REGEXP CONCAT('/', eqpmGrpCd, '/') AND grpLv = 1) FROM EqpmTbl M LEFT JOIN EqpmGrpTbl N ON M.eqpmGrpCd = N.eqpmGrpCd WHERE M.eqpmCd = A.eqpmCd) AS solution FROM CscoEqpmTbl A LEFT JOIN CscoTbl B ON A.cscoCd = B.cscoCd WHERE A.cscoCd IN ( SELECT B.cscoCd FROM CscoEqpmTbl A LEFT JOIN CscoTbl B ON A.cscoCd = B.cscoCd WHERE A.esmRefCd IN
#{item}
) ORDER BY solution, A.eqpmNm

		SELECT rptTp, count(*) AS CNT
		FROM ScrtCtrlRptTbl
        WHERE cpcgDtime BETWEEN #{startDtime} AND #{endDtime}
           AND cpcgYn = 'y'
           AND cscoCd = #{cscoCd}   
        GROUP BY rptTp

SELECT SCT, DATE_FORMAT(DT, '%Y-%m-%d') AS dateValue, contValue, eqpmNm, srcIp, destIp, destPort, prtcl, execSct FROM ( SELECT A.dryTp AS SCT, A.procDtime AS DT, B.bbsTit AS contValue, IF(ISNULL(A.tgtEqpmNm)||LENGTH(A.tgtEqpmNm)=0, A.etcEqpm, A.tgtEqpmNm) AS eqpmNm, '' AS srcIp, '' AS destIp, '' AS destPort, '' AS prtcl, '' AS execSct FROM WkDryTbl A LEFT JOIN BbsBaseTbl B ON A.bbsId = B.bbsId WHERE A.procDtime BETWEEN #{startDtime} AND #{endDtime} AND A.procSt = '2' AND A.dryTp in ('6','7','8') AND cscoCd = #{cscoCd}
UNION ALL
SELECT A.reqSct AS SCT, A.cpcgDtime AS DT, '' AS contValue, C.rqrNm AS eqpmNm, B.srcIp AS srcIp, B.destIp AS destIp, B.destPort AS destPort, B.prtcl AS prtcl, B.execSct AS execSct FROM CscoFwReqTbl A LEFT JOIN FwSetTbl B ON A.tcktCd = B.tcktCd LEFT JOIN CscoEqpmTbl C ON B.cscoCd = C.cscoCd AND B.eqpmCd = C.eqpmCd WHERE A.cpcgDtime BETWEEN #{startDtime} AND #{endDtime} AND A.cpcgYn = 'y' AND A.disposer IS NOT NULL AND A.procDtime IS NOT NULL AND A.cscoCd = #{cscoCd} ) T ORDER BY T.DT

SELECT reqSct, count(*) AS CNT
FROM CscoReqTbl
WHERE cpcgDtime BETWEEN #{startDtime} AND #{endDtime}
   AND cpcgYn = 'y'
   AND cscoCd = #{cscoCd}   
GROUP BY reqSct

SELECT bbsSct, COUNT(*) AS CNT FROM BbsBaseTbl WHERE bbsSct='B' AND regDtime BETWEEN #{startDtime} AND #{endDtime}
UNION ALL
SELECT bbsSct, COUNT(*) AS CNT FROM BbsBaseTbl WHERE bbsSct='T' AND regDtime BETWEEN #{startDtime} AND #{endDtime}
UNION ALL
SELECT bbsSct, COUNT(*) AS CNT FROM BbsBaseTbl WHERE bbsSct='P' AND regDtime BETWEEN #{startDtime} AND #{endDtime}
UNION ALL
SELECT 'R' AS bbsSct, COUNT(*) AS CNT FROM ( SELECT DATE_FORMAT(B.modDtime, '%Y. %m. %d') AS regDtime FROM RssTbl A LEFT JOIN BbsBaseTbl B ON A.bbsId = B.bbsId WHERE B.bbsSct = 'R' AND A.clippingYn = 'y' AND B.modDtime BETWEEN #{startDtime} AND #{endDtime} GROUP BY DATE_FORMAT(B.modDtime, '%Y-%m-%d') ) T

		SELECT DATE_FORMAT(cpcgDtime, '%Y%m%d') AS dateValue, esmRuleGroupPrntCd, esmRuleGroupPrntNm, COUNT(*) AS CNT
		FROM ViolAlrtRptTbl
		WHERE cpcgDtime BETWEEN #{startDtime} AND #{endDtime}
            AND cscoCd = #{cscoCd}
			AND esmRuleGroupPrntCd IS NOT NULL 
			AND esmRuleGroupCd IS NOT NULL
			AND cpcgYn = 'y'
			AND mailSndYn = 'y'
		GROUP BY DATE_FORMAT(cpcgDtime, '%m-%d'), esmRuleGroupPrntCd
		ORDER BY dateValue, CNT DESC;

		SELECT DATE_FORMAT(cpcgDtime, '%Y%m') AS dateValue, esmRuleGroupPrntCd, esmRuleGroupPrntNm, COUNT(*) AS CNT
		FROM ViolAlrtRptTbl
        WHERE cpcgDtime BETWEEN #{startDtime} AND #{endDtime}
			AND cscoCd = #{cscoCd}
            AND esmRuleGroupPrntCd IS NOT NULL 
            AND esmRuleGroupCd IS NOT NULL
            AND cpcgYn = 'y'
            AND mailSndYn = 'y'
		GROUP BY DATE_FORMAT(cpcgDtime, '%Y-%m'), esmRuleGroupPrntCd
		ORDER BY dateValue, CNT DESC;

SELECT esmRuleGroupPrntCd, esmRuleGroupPrntNm, COUNT(*) AS CNT
FROM ViolAlrtRptTbl
WHERE cpcgDtime BETWEEN #{startDtime} AND #{endDtime}

    AND cscoCd = #{cscoCd}

    AND esmRuleGroupPrntCd IS NOT NULL 
    AND esmRuleGroupCd IS NOT NULL
    AND cpcgYn = 'y'
    AND mailSndYn = 'y'

    AND srcIp = #{srcIp}


    AND destIp = #{destIp}


    AND srcGeo = #{srcGeo}


    AND destPort = #{destPort}

GROUP BY esmRuleGroupPrntCd
ORDER BY CNT DESC;

		SELECT esmRuleGroupCd, esmRuleGroupNm, COUNT(*) AS CNT
		FROM ViolAlrtRptTbl
		WHERE cpcgDtime BETWEEN #{startDtime} AND #{endDtime}
            AND esmRuleGroupPrntCd IS NOT NULL 
            AND esmRuleGroupCd IS NOT NULL
            AND cpcgYn = 'y'
            AND mailSndYn = 'y'

            AND cscoCd = #{cscoCd}


            AND srcIp = #{srcIp}


            AND destIp = #{destIp}


            AND srcGeo = #{srcGeo}


            AND destPort = #{destPort}

            AND esmRuleGroupPrntCd = #{prntCd}
		GROUP BY esmRuleGroupCd
		ORDER BY CNT DESC

          LIMIT #{limit}

